import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import sdk from 'matrix-js-sdk';

class App extends Component{
  constructor(props){
    super(props)

  }


  render() {
    return(
      <div className="container">
        <Header />
        <Login />
      </div>
    )
  }
}

class Login extends Component{
  constructor(props){
    super(props)
    this.state = {
      serverUrl: "https://matrix.org",
      userID: "",
      password: "",
      stateOfConnection:false
    }

    //Bind des 'this' sur les fonctions de classe : 
    this.updateValueHandle = this.updateValueHandle.bind(this)
    this.submitHandle = this.submitHandle.bind(this)
  }

  updateValueHandle(e){
    //Changement de l'état du login lors du renseignement de l'userID et du paswword : 
    const eventID = e.target.id
    const eventValue = e.target.value
    this.setState(state => ({
      [eventID]: eventValue,
    }))
  }

  submitHandle(e){
    //Création du client et tentative de connexion : 

    const client = sdk.createClient(this.state.serverUrl)
    client.login("m.login.password", {"user": this.state.userID, "password": this.state.password}).then((response) => {
      console.log(response.access_token)
    })

    //Synchronisation avec le serveur Matrix : 
    client.startClient()
    client.once('sync', function(state, prevState, res) {
      console.log(state); // state will be 'PREPARED' when the client is ready to use
    })

    client.on("Room.timeline", function(event, room, toStartOfTimeline) {
      console.log(event.event);
    });
    e.preventDefault()
  }

  render() {
    return(
      <div className="col-12 offset-sm-1 text-center">
        <form className="form-signin justify-content-center" onSubmit={this.submitHandle}>
        <div className="form-group">
          <label htmlFor="user">User ID : </label>
          <input className="form-control" type="text" id="userID" onChange={this.updateValueHandle}/>
        </div>
        <div className="form-group">
          <label htmlFor="password">Password : </label>
          <input className="form-control" type="text" id="password" onChange={this.updateValueHandle}/>
        </div>
          <button className="btn btn-primary" type="submit">Submit</button>
      </form>
      </div>

    )
  }
}

function Header(){
  return(
    <header>
      <h1 >Watcha Client</h1>
    </header> 
  )
}

export default App;
