  constructor(props){
    super(props)
    this.state = {
      client: null,
      stateOfConnection:false
    }  
  }

  connectionToMatrixServer(){
    this.state.client = sdk.createClient("https://matrix.org")
    this.state.client.login("m.login.password", {"user": "kicol", "password": "Akleevx73."})
  }

  render() {
    return(
      <div>
        <Header />
        <Login />
      </div>
    )
  }
}

