import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import sdk from 'matrix-js-sdk';

class App extends Component{
  constructor(props){
    super(props)
    this.state = {
      client: null,

    }  
  }


  render() {
    return(
      <div className="container">
        <Header />
        <Login />
      </div>
    )
  }
}

class Login extends Component{
  constructor(props){
    super(props)
    this.state = {
      serverUrl: "https://matrix.org",
      userID: "",
      password: "",
      stateOfConnection:false
    }
    //Bind des 'this' sur les fonctions de classe : 
    this.updateValueHandle = this.updateValueHandle.bind(this)
    this.submitHandle = this.submitHandle.bind(this)
  }

  updateValueHandle(){
    this.setState(state => ({
      userID: this.target.value,
      password: this.target.value,
    }))
  }

  /*submitHandle(){
    //Tentative de connexion au serveur Matrix :
    const matrixClient = sdk.createClient(this.state.serverUrl)
    this.state.client.login("m.login.password", {"user": this.state.userID, "password": this.state.password})
    //Changement de l'état du composant Login : 

  }*/

  render() {
    return(
      <div className="col-6">
        <form onSubmit={this.submitHandle}>
        <div className="form-group">
          <label for="user">User ID : </label>
          <input className="form-control" id="user" onChange={this.updateValueHandle}/>
        </div>
        <div className="form-group">
          <label for="password">Password : </label>
          <input className="form-control" id="password" onChange={this.updateValueHandle}/>
        </div>
          <button className="btn btn-primary" type="submit">Submit</button>
        </form>
      </div>
    )
  }
}
